/**
 * 
 * ADB
 */
export class DiagnosticResult {
  constructor(
    public readonly id: string,
    public readonly name: string,
    public readonly status: DiagnosticStatus,
    public readonly message: string,
    public readonly details?: string,
    public readonly suggestion?: string,
    public readonly canAutoFix: boolean = false,
    public readonly autoFixAction?: () => Promise<boolean>,
    public readonly timestamp: Date = new Date(),
    public readonly category: DiagnosticCategory = DiagnosticCategory.GENERAL,
    public readonly source?: string,
    public readonly deviceId?: string,
    public readonly sessionId?: string
  ) {}

  /**
   * 
   */
  static success(id: string, name: string, message: string): DiagnosticResult {
    return new DiagnosticResult(id, name, DiagnosticStatus.SUCCESS, message);
  }

  /**
   * 
   */
  static warning(
    id: string,
    name: string,
    message: string,
    suggestion?: string
  ): DiagnosticResult {
    return new DiagnosticResult(
      id,
      name,
      DiagnosticStatus.WARNING,
      message,
      undefined,
      suggestion
    );
  }

  /**
   * 
   */
  static error(
    id: string,
    name: string,
    message: string,
    details?: string,
    suggestion?: string,
    canAutoFix: boolean = false,
    autoFixAction?: () => Promise<boolean>
  ): DiagnosticResult {
    return new DiagnosticResult(
      id,
      name,
      DiagnosticStatus.ERROR,
      message,
      details,
      suggestion,
      canAutoFix,
      autoFixAction
    );
  }

  /**
   * 
   */
  isError(): boolean {
    return this.status === DiagnosticStatus.ERROR;
  }

  /**
   * 
   */
  isAutoFixable(): boolean {
    return this.canAutoFix && this.autoFixAction !== undefined;
  }

  /**
   * 
   */
  async executeAutoFix(): Promise<boolean> {
    if (!this.isAutoFixable()) {
      throw new Error(` ${this.name} `);
    }
    return await this.autoFixAction!();
  }

  /**
   * 
   */
  withStatus(status: DiagnosticStatus, message?: string): DiagnosticResult {
    return new DiagnosticResult(
      this.id,
      this.name,
      status,
      message || this.message,
      this.details,
      this.suggestion,
      this.canAutoFix,
      this.autoFixAction,
      new Date(),
      this.category,
      this.source,
      this.deviceId,
      this.sessionId
    );
  }
}

/**
 * 
 */
export enum DiagnosticStatus {
  SUCCESS = 'success',
  WARNING = 'warning',
  ERROR = 'error',
  RUNNING = 'running'
}

/**
 * 
 */
export enum DiagnosticCategory {
  GENERAL = 'general',
  PATH_DETECTION = 'path_detection',
  DEVICE_CONNECTION = 'device_connection',
  SERVER_STATUS = 'server_status',
  PERMISSIONS = 'permissions'
}

/**
 * 
 */
export class DiagnosticSummary {
  constructor(
    public readonly totalChecks: number,
    public readonly successCount: number,
    public readonly warningCount: number,
    public readonly errorCount: number,
    public readonly autoFixableCount: number,
    public readonly lastRun: Date = new Date()
  ) {}

  /**
   * 
   */
  static fromResults(results: DiagnosticResult[]): DiagnosticSummary {
    const totalChecks = results.length;
    const successCount = results.filter(r => r.status === DiagnosticStatus.SUCCESS).length;
    const warningCount = results.filter(r => r.status === DiagnosticStatus.WARNING).length;
    const errorCount = results.filter(r => r.status === DiagnosticStatus.ERROR).length;
    const autoFixableCount = results.filter(r => r.isAutoFixable()).length;

    return new DiagnosticSummary(
      totalChecks,
      successCount,
      warningCount,
      errorCount,
      autoFixableCount
    );
  }

  /**
   * 
   */
  hasErrors(): boolean {
    return this.errorCount > 0;
  }

  /**
   * 
   */
  hasWarnings(): boolean {
    return this.warningCount > 0;
  }

  /**
   * 
   */
  isHealthy(): boolean {
    return !this.hasErrors() && !this.hasWarnings();
  }

  /**
   * 
   */
  getHealthPercentage(): number {
    if (this.totalChecks === 0) return 100;
    return Math.round((this.successCount / this.totalChecks) * 100);
  }
}

/**
 *  - 
 * Android
 */
export class Device {
  constructor(
    public readonly id: string,
    public readonly name: string,
    public readonly status: DeviceStatus,
    public readonly type: DeviceType,
    public readonly model?: string,
    public readonly product?: string,
    public readonly transportId?: string,
    public readonly lastSeen: Date = new Date(),
    public readonly properties: Record<string, string> = {}
  ) {}

  /**
   * 
   */
  static fromRaw(data: {
    id: string;
    status: string;
    model?: string;
    product?: string;
    transport_id?: string;
    type?: string;
  }): Device {
    return new Device(
      data.id,
      data.model || data.product || `Device-${data.id.slice(0, 8)}`,
      Device.parseStatus(data.status),
      Device.parseType(data.type || data.id),
      data.model,
      data.product,
      data.transport_id,
      new Date()
    );
  }

  /**
   * 
   */
  private static parseStatus(status: string): DeviceStatus {
    switch (status.toLowerCase()) {
      case 'device':
        return DeviceStatus.ONLINE;
      case 'offline':
        return DeviceStatus.OFFLINE;
      case 'unauthorized':
        return DeviceStatus.UNAUTHORIZED;
      default:
        return DeviceStatus.UNKNOWN;
    }
  }

  /**
   * 
   */
  private static parseType(identifier: string): DeviceType {
    if (identifier.includes('emulator') || identifier.startsWith('127.0.0.1')) {
      return DeviceType.EMULATOR;
    }
    if (identifier.includes(':')) {
      return DeviceType.WIFI;
    }
    return DeviceType.USB;
  }

  /**
   * 
   */
  isOnline(): boolean {
    return this.status === DeviceStatus.ONLINE;
  }

  /**
   * 
   */
  isEmulator(): boolean {
    return this.type === DeviceType.EMULATOR;
  }

  /**
   * 
   */
  getDisplayName(): string {
    if (this.model) return this.model;
    if (this.product) return this.product;
    return this.isEmulator() ? ` ${this.id}` : ` ${this.id}`;
  }

  /**
   * 
   */
  withStatus(status: DeviceStatus): Device {
    return new Device(
      this.id,
      this.name,
      status,
      this.type,
      this.model,
      this.product,
      this.transportId,
      new Date(),
      this.properties
    );
  }

  /**
   * 
   */
  withProperties(properties: Record<string, string>): Device {
    return new Device(
      this.id,
      this.name,
      this.status,
      this.type,
      this.model,
      this.product,
      this.transportId,
      this.lastSeen,
      { ...this.properties, ...properties }
    );
  }
}

