// module: ui | layer: ui | role: component
// summary: UI 

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Button,
  Space,
  Typography,
  Progress,
  Steps,
  Tag,
  Modal,
  Badge,
  Timeline,
  Drawer,
  Switch,
} from 'antd';
import {
  PlayCircleOutlined,
  PauseCircleOutlined,
  StopOutlined,
  ReloadOutlined,
  SettingOutlined,
  EyeOutlined,
  ExclamationCircleOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
  BugOutlined,
  ThunderboltOutlined,
  DownloadOutlined,
} from '@ant-design/icons';
import { invoke } from '@tauri-apps/api/core';

const { Title, Text, Paragraph } = Typography;
const { Step } = Steps;

// 
enum ExecutionStatus {
  IDLE = 'idle',
  RUNNING = 'running',
  PAUSED = 'paused',
  COMPLETED = 'completed',
  FAILED = 'failed',
  STOPPED = 'stopped'
}

// 
interface StepStatus {
  id: string;
  name: string;
  status: 'wait' | 'process' | 'finish' | 'error';
  startTime?: Date;
  endTime?: Date;
  duration?: number;
  error?: string;
  logs: LogEntry[];
}

// 
interface LogEntry {
  id: string;
  timestamp: Date;
  level: 'info' | 'warn' | 'error' | 'debug';
  message: string;
  stepId?: string;
  data?: any;
}

// 
interface ExecutionStats {
  totalSteps: number;
  completedSteps: number;
  failedSteps: number;
  totalDuration: number;
  averageStepTime: number;
  successRate: number;
}

// 
interface ScriptInfo {
  id: string;
  name: string;
  description: string;
  steps: any[];
  device?: string;
}

//

interface ScriptExecutionMonitorProps {
  script?: ScriptInfo;
  onBack?: () => void;
}

const ScriptExecutionMonitor: React.FC<ScriptExecutionMonitorProps> = ({ script, onBack }) => {
  const [executionStatus, setExecutionStatus] = useState<ExecutionStatus>(ExecutionStatus.IDLE);
  const [stepStatuses, setStepStatuses] = useState<StepStatus[]>([]);
  const [logs, setLogs] = useState<LogEntry[]>([]);
  const [currentStepIndex, setCurrentStepIndex] = useState<number>(-1);
  const [stats, setStats] = useState<ExecutionStats>({
    totalSteps: 0,
    completedSteps: 0,
    failedSteps: 0,
    totalDuration: 0,
    averageStepTime: 0,
    successRate: 0
  });
  const [showSettings, setShowSettings] = useState(false);
  const [showLogs, setShowLogs] = useState(false);
  const [autoScroll, setAutoScroll] = useState(true);
  const [executionId, setExecutionId] = useState<string>('');

  // 
  useEffect(() => {
    if (script?.steps) {
      const initialSteps: StepStatus[] = script.steps.map((step, index) => ({
        id: `step_${index}`,
        name: step.name || ` ${index + 1}`,
        status: 'wait',
        logs: []
      }));
      setStepStatuses(initialSteps);
      setStats(prev => ({ ...prev, totalSteps: script.steps.length }));
    }
  }, [script]);

  // 
  const addLog = (level: LogEntry['level'], message: string, stepId?: string, data?: any) => {
    const logEntry: LogEntry = {
      id: `log_${Date.now()}_${Math.random()}`,
      timestamp: new Date(),
      level,
      message,
      stepId,
      data
    };
    
    setLogs(prev => [...prev, logEntry]);
    
    // 
    if (stepId) {
      setStepStatuses(prev => prev.map(step => 
        step.id === stepId 
          ? { ...step, logs: [...step.logs, logEntry] }
          : step
      ));
    }
  };

  //

);
    }
  };

  // 
  const startExecution = async () => {
    if (!script) return;

    try {
      setExecutionStatus(ExecutionStatus.RUNNING);
      setCurrentStepIndex(0);
      
      const execId = `exec_${Date.now()}`;
      setExecutionId(execId);
      
      addLog('info', `: ${script.name}`);
      addLog('info', `ID: ${execId}`);
      addLog('info', `: ${script.steps.length}`);

      // 
      const result = await invoke('execute_script_with_monitoring', {
        script: {
          id: script.id,
          name: script.name,
          steps: script.steps
        },
        executionId: execId
      });

      addLog('info', '', undefined, result);
      
    } catch (error) {
      console.error(':', error);
      addLog('error', `: ${error}`);
      setExecutionStatus(ExecutionStatus.FAILED);
    }
  };

  // 
  const pauseExecution = async () => {
    try {
      setExecutionStatus(ExecutionStatus.PAUSED);
      addLog('warn', '');
      
      await invoke('pause_script_execution', { executionId });
    } catch (error) {
      console.error(':', error);
      addLog('error', `: ${error}`);
    }
  };

  // 
  const resumeExecution = async () => {
    try {
      setExecutionStatus(ExecutionStatus.RUNNING);
      addLog('info', '');
      
      await invoke('resume_script_execution', { executionId });
    } catch (error) {
      console.error(':', error);
      addLog('error', `: ${error}`);
    }
  };

  // 
  const stopExecution = async () => {
    try {
      setExecutionStatus(ExecutionStatus.STOPPED);
      addLog('warn', '');
      
      await invoke('stop_script_execution', { executionId });
    } catch (error) {
      console.error(':', error);
      addLog('error', `: ${error}`);
    }
  };

  // 
  const restartExecution = () => {
    // 
    setExecutionStatus(ExecutionStatus.IDLE);
    setCurrentStepIndex(-1);
    setLogs([]);
    setStepStatuses(prev => prev.map(step => ({
      ...step,
      status: 'wait',
      startTime: undefined,
      endTime: undefined,
      duration: undefined,
      error: undefined,
      logs: []
    })));
    
    // 
    setTimeout(() => startExecution(), 100);
  };

  // 
  const getProgress = () => {
    if (stepStatuses.length === 0) return 0;
    const completed = stepStatuses.filter(s => s.status === 'finish').length;
    return Math.round((completed / stepStatuses.length) * 100);
  };

  //

};

  // 
  const exportReport = () => {
    const report = {
      script: script?.name,
      executionId,
      status: executionStatus,
      stats,
      steps: stepStatuses,
      logs: logs,
      timestamp: new Date().toISOString()
    };

    const dataStr = JSON.stringify(report, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `execution_report_${executionId}.json`;
    link.click();
    URL.revokeObjectURL(url);
  };

  //

};

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ maxWidth: 1400, margin: '0 auto' }}>
        {/*  */}
        <Card style={{ marginBottom: 16 }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <Title level={3} style={{ margin: 0 }}>
                 
              </Title>
              <Text type="secondary">
                {script?.name || ''} | ID: {executionId || ''}
              </Text>
            </div>
            <Space>
              <Button onClick={onBack}></Button>
              <Button 
                icon={<SettingOutlined />} 
                onClick={() => setShowSettings(true)}
              >
                
              </Button>
              <Button 
                icon={<DownloadOutlined />} 
                onClick={exportReport}
                disabled={executionStatus === ExecutionStatus.IDLE}
              >
                
              </Button>
            </Space>
